import React from "react";
import {indexServices} from "../../services/index";

class Users extends React.Component {
  state = {
    githubUsers: null,
    users: null
  };

  componentWillUnmount(){
    indexServices.abort();
  };

  getUser = async () => {
    const res = await indexServices.getUsers();
    this.setState({
      githubUsers: res ? res.data.results : []
    });
  };

  getUsers = async () => {
    const res = await indexServices.getUser();
    this.setState({
        users: res ? res.data : []
    })

  }



  render() {
    return (
      <>
        <input type="button" value="Show" onClick={this.getUser} />
        <input type="button" value="Cancel" onClick={this.getUsers}/>

        <div>
          {this.state.githubUsers &&
            this.state.githubUsers.map((value, key) => (
              <h2 key={value.id}>name : {value.email}</h2>
            ))}
            {this.state.users &&
            this.state.users.slice(0,1).map((value, key) => (
              <h2 key={value.id}>name : {value.login}</h2>
            ))}
        </div>
      </>
    );
  }
}
export default Users;
