import React from "react";
import getUsers from "../../services/index";

class Users extends React.Component {
  state = {
    githubUsers: null
  };

  getUser = async () => {
    const res = await getUsers();
    this.setState({
      githubUsers: res.data
    });
  };

  componentWillUnmount = () => {
    const abortController = new AbortController();
    this.abortController.abort();
  };

  render() {
      console.log(this.abortController, '--this.abortController--')
    return (
      <>
        <input type="button" value="Show" onClick={this.getUser} />
        <input type="button" value="Cancel" />

        <div>
          {this.state.githubUsers &&
            this.state.githubUsers.map((value, key) => (
              <h2 key={value.id}>name : {value.login}</h2>
            ))}
        </div>
      </>
    );
  }
}
export default Users;
